    #{startAttNo}+#{idx}
   , #{boNo,jdbcType=NUMERIC}
   , #{attatch.attFilename,jdbcType=VARCHAR}
   , #{attatch.attSavename,jdbcType=VARCHAR}
   , #{attatch.attMime,jdbcType=VARCHAR}
   , #{attatch.attFilesize,jdbcType=NUMERIC}
   , #{attatch.attFancysize,jdbcType=VARCHAR}
